mapper.select:
SELECT mi.mi_seq AS guest_seq, rev.rev_content AS guest_review, rev.rev_reg_dt AS guest_rev_dt, mi.mi_name AS guest_name, mimg.mimg_file AS guest_img, mi2.mi_seq AS host_seq, mi2.mi_name AS host_name, mimg2.mimg_file AS host_img, rpl.rpl_content AS host_reply, rpl.rpl_reg_dt AS host_rpl_dt FROM review_info rev LEFT JOIN member_info mi ON rev.rev_mi_seq = mi.mi_seq LEFT JOIN member_img mimg ON mi.mi_seq = mimg.mimg_mi_seq LEFT JOIN review_reply rpl ON rev.rev_seq = rpl.rpl_rev_seq LEFT JOIN member_info mi2 ON rpl.rpl_mi_seq = mi2.mi_seq LEFT JOIN member_img mimg2 ON mi2.mi_seq = mimg2.mimg_mi_seq WHERE rev.rev_public = 1 AND rev.rev_hi_seq = #{house_seq}
AND ( mi.mi_name like concat('%',#{keyword},'%') OR rev.rev_content like concat('%',#{keyword},'%') )
LIMIT #{limit}

SELECT COUNT(*) AS house_review_cnt
FROM review_info
WHERE rev_hi_seq = #{house_seq}
